import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import {
  insertUserSchema,
  insertAnnouncementSchema,
  insertWishListItemSchema,
  insertAccountSchema,
  configSchema,
  loginSchema,
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(
  httpServer: Server,
  app: Express
): Promise<Server> {
  // Authentication
  app.post("/api/auth/login", async (req, res) => {
    try {
      const result = loginSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ success: false, message: "Identifiant et mot de passe requis" });
      }

      const { username, password } = result.data;
      const account = await storage.authenticateAccount(username, password);

      if (!account) {
        return res.status(401).json({ success: false, message: "Identifiant ou mot de passe incorrect" });
      }

      res.json({
        success: true,
        account: {
          id: account.id,
          username: account.username,
          displayName: account.displayName,
          role: account.role,
        },
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ success: false, message: "Erreur serveur" });
    }
  });

  // Accounts routes
  app.get("/api/accounts", async (_req, res) => {
    try {
      const accounts = await storage.getAccounts();
      // Don't return passwords
      const safeAccounts = accounts.map(({ password, ...rest }) => rest);
      res.json(safeAccounts);
    } catch (error) {
      console.error("Get accounts error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.post("/api/accounts", async (req, res) => {
    try {
      const result = insertAccountSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides", details: result.error });
      }

      // Check if username already exists
      const existing = await storage.getAccountByUsername(result.data.username);
      if (existing) {
        return res.status(400).json({ error: "Cet identifiant existe deja" });
      }

      const account = await storage.createAccount(result.data);
      const { password, ...safeAccount } = account;
      res.status(201).json(safeAccount);
    } catch (error) {
      console.error("Create account error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.put("/api/accounts/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const result = insertAccountSchema.partial().safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides" });
      }

      // Check if username already exists (if changing username)
      if (result.data.username) {
        const existing = await storage.getAccountByUsername(result.data.username);
        if (existing && existing.id !== id) {
          return res.status(400).json({ error: "Cet identifiant existe deja" });
        }
      }

      const account = await storage.updateAccount(id, result.data);
      if (!account) {
        return res.status(404).json({ error: "Compte non trouve" });
      }
      const { password, ...safeAccount } = account;
      res.json(safeAccount);
    } catch (error) {
      console.error("Update account error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.delete("/api/accounts/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteAccount(id);
      if (!deleted) {
        return res.status(404).json({ error: "Compte non trouve" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete account error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  // Config routes
  app.get("/api/config", async (_req, res) => {
    try {
      const config = await storage.getConfig();
      res.json(config);
    } catch (error) {
      console.error("Get config error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.put("/api/config/passwords", async (req, res) => {
    try {
      const result = configSchema.partial().safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides" });
      }
      const config = await storage.updatePasswords(result.data);
      res.json(config);
    } catch (error) {
      console.error("Update passwords error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  // Users routes
  app.get("/api/users", async (_req, res) => {
    try {
      const users = await storage.getUsers();
      res.json(users);
    } catch (error) {
      console.error("Get users error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.post("/api/users", async (req, res) => {
    try {
      const result = insertUserSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides", details: result.error });
      }
      const user = await storage.createUser(result.data);
      res.status(201).json(user);
    } catch (error) {
      console.error("Create user error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.put("/api/users/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const result = insertUserSchema.partial().safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides" });
      }
      const user = await storage.updateUser(id, result.data);
      if (!user) {
        return res.status(404).json({ error: "Utilisateur non trouve" });
      }
      res.json(user);
    } catch (error) {
      console.error("Update user error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.delete("/api/users/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteUser(id);
      if (!deleted) {
        return res.status(404).json({ error: "Utilisateur non trouve" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete user error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  // Lists routes
  app.get("/api/lists", async (_req, res) => {
    try {
      const lists = await storage.getLists();
      res.json(lists);
    } catch (error) {
      console.error("Get lists error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.get("/api/lists/current", async (req, res) => {
    try {
      const username = req.query.username as string;
      if (!username) {
        return res.status(400).json({ error: "Username requis" });
      }
      const items = await (storage as any).getWorkingList(username);
      res.json({ 
        id: "current",
        username,
        createdAt: new Date().toISOString(),
        items 
      });
    } catch (error) {
      console.error("Get current list error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.post("/api/lists/items", async (req, res) => {
    try {
      const { username, ...itemData } = req.body;
      if (!username) {
        return res.status(400).json({ error: "Username requis" });
      }
      const result = insertWishListItemSchema.safeParse(itemData);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides", details: result.error });
      }
      const item = await storage.addItemToList(username, result.data);
      res.status(201).json(item);
    } catch (error) {
      console.error("Add item error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.put("/api/lists/items/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const { username, ...itemData } = req.body;
      if (!username) {
        return res.status(400).json({ error: "Username requis" });
      }
      const item = await storage.updateItem(id, username, itemData);
      if (!item) {
        return res.status(404).json({ error: "Article non trouve" });
      }
      res.json(item);
    } catch (error) {
      console.error("Update item error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.delete("/api/lists/items/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const { username } = req.body;
      if (!username) {
        return res.status(400).json({ error: "Username requis" });
      }
      const deleted = await storage.deleteItem(id, username);
      if (!deleted) {
        return res.status(404).json({ error: "Article non trouve" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete item error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.post("/api/lists/send", async (req, res) => {
    try {
      const { username } = req.body;
      if (!username) {
        return res.status(400).json({ error: "Username requis" });
      }
      const list = await (storage as any).sendList(username);
      if (!list) {
        return res.status(400).json({ error: "Liste vide" });
      }
      res.status(201).json(list);
    } catch (error) {
      console.error("Send list error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.delete("/api/lists/reset", async (_req, res) => {
    try {
      await storage.deleteAllLists();
      res.json({ success: true });
    } catch (error) {
      console.error("Reset lists error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  // Announcements routes
  app.get("/api/announcements", async (_req, res) => {
    try {
      const announcements = await storage.getAnnouncements();
      res.json(announcements);
    } catch (error) {
      console.error("Get announcements error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.post("/api/announcements", async (req, res) => {
    try {
      const result = insertAnnouncementSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides", details: result.error });
      }
      const announcement = await storage.createAnnouncement(result.data);
      res.status(201).json(announcement);
    } catch (error) {
      console.error("Create announcement error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.put("/api/announcements/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const result = insertAnnouncementSchema.partial().safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Donnees invalides" });
      }
      const announcement = await storage.updateAnnouncement(id, result.data);
      if (!announcement) {
        return res.status(404).json({ error: "Annonce non trouvee" });
      }
      res.json(announcement);
    } catch (error) {
      console.error("Update announcement error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  app.delete("/api/announcements/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteAnnouncement(id);
      if (!deleted) {
        return res.status(404).json({ error: "Annonce non trouvee" });
      }
      res.json({ success: true });
    } catch (error) {
      console.error("Delete announcement error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  // Stats route
  app.get("/api/stats", async (_req, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      console.error("Get stats error:", error);
      res.status(500).json({ error: "Erreur serveur" });
    }
  });

  return httpServer;
}

shared/schema.ts.content:
import { z } from "zod";

// Account schema for authentication
export const accountSchema = z.object({
  id: z.string(),
  username: z.string().min(1, "L'identifiant est requis"),
  password: z.string().min(1, "Le mot de passe est requis"),
  displayName: z.string().min(1, "Le nom est requis"),
  role: z.enum(["user", "parent", "developer"]),
  createdAt: z.string(),
});

export type Account = z.infer<typeof accountSchema>;

export const insertAccountSchema = accountSchema.omit({ id: true, createdAt: true });
export type InsertAccount = z.infer<typeof insertAccountSchema>;

// Config schema for passwords (legacy - kept for backward compatibility)
export const configSchema = z.object({
  userPassword: z.string(),
  parentPassword: z.string(),
  devPassword: z.string(),
});

export type Config = z.infer<typeof configSchema>;

// Wish list item schema
export const wishListItemSchema = z.object({
  id: z.string(),
  title: z.string().min(1, "Le titre est requis"),
  description: z.string().optional(),
  quantity: z.number().min(1).default(1),
  image: z.string().optional(),
  imageUrl: z.string().url().optional().or(z.literal("")),
});

export type WishListItem = z.infer<typeof wishListItemSchema>;

export const insertWishListItemSchema = wishListItemSchema.omit({ id: true });
export type InsertWishListItem = z.infer<typeof insertWishListItemSchema>;

// Wish list schema
export const wishListSchema = z.object({
  id: z.string(),
  username: z.string().min(1, "Le nom est requis"),
  createdAt: z.string(),
  items: z.array(wishListItemSchema),
});

export type WishList = z.infer<typeof wishListSchema>;

export const insertWishListSchema = wishListSchema.omit({ id: true, createdAt: true });
export type InsertWishList = z.infer<typeof insertWishListSchema>;

// User schema for developer management
export const userSchema = z.object({
  id: z.string(),
  username: z.string().min(1, "Le nom d'utilisateur est requis"),
  email: z.string().email("Email invalide").optional(),
  createdAt: z.string(),
});

export type User = z.infer<typeof userSchema>;

export const insertUserSchema = userSchema.omit({ id: true, createdAt: true });
export type InsertUser = z.infer<typeof insertUserSchema>;

// Announcement schema
export const announcementSchema = z.object({
  id: z.string(),
  title: z.string().min(1, "Le titre est requis"),
  content: z.string().min(1, "Le contenu est requis"),
  createdAt: z.string(),
  isActive: z.boolean().default(true),
});

export type Announcement = z.infer<typeof announcementSchema>;

export const insertAnnouncementSchema = announcementSchema.omit({ id: true, createdAt: true });
export type InsertAnnouncement = z.infer<typeof insertAnnouncementSchema>;

// Authentication schemas
export const loginSchema = z.object({
  username: z.string().min(1, "L'identifiant est requis"),
  password: z.string().min(1, "Le mot de passe est requis"),
});

export type LoginInput = z.infer<typeof loginSchema>;

// Role type
export type UserRole = "user" | "parent" | "developer";

// Stats schema
export const statsSchema = z.object({
  totalLists: z.number(),
  totalUsers: z.number(),
  totalItems: z.number(),
  totalAnnouncements: z.number(),
  totalAccounts: z.number(),
});

export type Stats = z.infer<typeof statsSchema>;

